mapper.update:
UPDATE customer_address
SET `name` = #{name}, address = #{address},lng	=	#{lng},lat	=	#{lat},phone_prefix = #{phonePrefix},phone = #{phone}
WHERE id = #{id}

UPDATE customer_address
SET is_default = false
WHERE customer_id = #{customerId}

UPDATE customer_address
SET is_default = true
WHERE id = #{id}
