mapper.select:
SELECT ed.id, ed.user_id, ed.number, ed.`name`, ed.date_of_birth, ed.phone_prefix, ed.phone, ed.email, ed.preset_job_ids, ed.head_url, ed.company_id, ed.id_card, ed.address1, ed.address2, ed.address3, ed.address4, ed.lat, ed.lng, ed.work_year, ed.education_background, ed.account_line, ed.describes, ed.scope_of_order, ed.number_of_orders, ed.working_area, ed.eng_name, ed.phone_url, ed.sex, ed.record_of_formal_schooling, ed.blacklist_flag, ed.star_rating, ed.create_time, ed.update_time, ed.last_reviser_id, cd.no_certified_company FROM employees_details ed LEFT JOIN company_details cd ON ed.company_id = cd.id
ed.id = #{query.id}
and ed.company_id = #{query.companyId}
and ed.name LIKE CONCAT('%',#{query.name},'%')
and ed.account_line LIKE CONCAT('%',#{query.accountLine},'%')

SELECT ed.* FROM employees_details ed LEFT JOIN company_details cd ON ed.company_id = cd.id
cd.is_personal = 1
and ed.id = #{query.id}
and ed.company_id = #{query.companyId}
and ed.name LIKE CONCAT('%',#{query.name},'%')
and ed.account_line LIKE CONCAT('%',#{query.accountLine},'%')

mapper.update:
UPDATE employees_details
SET head_url = #{headUrl}
WHERE user_id = #{userId}

UPDATE employees_details
SET blacklist_flag = #{action}
WHERE id = #{employeesId}

UPDATE employees_details
SET working_area = #{areaIds}
WHERE id = #{employeesId}

UPDATE employees_details
SET preset_job_ids = #{presetJobIds}
WHERE id = #{employeesId}

