mapper.update:
UPDATE order_details
SET order_state = #{status},
    completion_date_time = #{time}
WHERE number = #{number}

UPDATE order_details
SET order_state = #{status}
WHERE number = #{number}

mapper.select:
SELECT c.order_retention_time
FROM company_details c right join employees_details e on e.company_id = c.id
WHERE e.id = #{employeesId}
